import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isServicesOpen, setIsServicesOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
    setIsServicesOpen(false);
  };

  const toggleServices = () => {
    setIsServicesOpen(!isServicesOpen);
  };

  const services = [
    { name: 'Limpieza Regular', href: '#regular-cleaning' },
    { name: 'Mantenimiento Químicos', href: '#chemical-maintenance' },
    { name: 'Limpieza Profunda', href: '#deep-cleaning' },
    { name: 'Inspección y Consultoría', href: '#inspection-consulting' }
  ];

  return (
    <header className="header">
      <div className="header-container">
        <div className="logo">
          <h2>PoolPro Ivan</h2>
          <span>Cathedral City Pool Services</span>
        </div>
        
        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
          <a href="#home" className="nav-link" onClick={closeMenu}>Inicio</a>
          
          <div className="nav-dropdown">
            <button 
              className="nav-link dropdown-toggle" 
              onClick={toggleServices}
              onMouseEnter={() => setIsServicesOpen(true)}
            >
              Servicios
            </button>
            <div className={`dropdown-menu ${isServicesOpen ? 'show' : ''}`}
                 onMouseLeave={() => setIsServicesOpen(false)}>
              {services.map((service, index) => (
                <a 
                  key={index}
                  href={service.href} 
                  className="dropdown-item" 
                  onClick={closeMenu}
                >
                  {service.name}
                </a>
              ))}
            </div>
          </div>
          
          <a href="#about" className="nav-link" onClick={closeMenu}>Acerca de</a>
          <a href="#contact" className="nav-link" onClick={closeMenu}>Contacto</a>
        </nav>

        <button 
          className={`mobile-menu-btn ${isMenuOpen ? 'active' : ''}`} 
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </header>
  );
};

export default Header;
